import React, { useState, useEffect } from "react";
import { Card, Form, Button, Col } from "react-bootstrap";
import PrintData from "./PrintData";
import UserList from "./UserList";

const DataForm = () => {
  const [userInfo, setUserInfo] = useState({
    userName: "",
    pokemonName: "",
  });
  const [userList, setUserList] = useState([]);
  const [pokemon, setPokemon] = useState("");
  const [validate, setValidate] = useState(false);

  const handleUserName = (e) => {
    setUserInfo({ ...userInfo, userName: e.target.value });
  };

  const handlePokemon = (e) => {
    setPokemon(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (userName) {
      let temp = userList;
      //   temp.push(userName);
      //   setUserList(temp);
      //   setUserName("");
      temp.push({ user: userName, pokemonName: pokemon });
      setUserList(temp);
      setValidate(true);
    } else {
      console.log("Add some value");
    }
  };

  const unmount = () => {
    console.log("Component unmount called");
  };

  useEffect(() => {
    console.log("Data form useEffect");
    return unmount;
  }, []);

  return (
    <Card style={{ width: "30rem", padding: "20px" }}>
      <h1
        style={{
          textAlign: "center",
          fontWeight: "bold",
          marginBottom: "40px",
        }}
      >
        FORM
      </h1>
      <Form>
        <Form.Row>
          <Form.Group as={Col}>
            <Form.Label style={{ fontWeight: "bold" }}>User Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Name"
              onChange={handleUserName}
              value={userInfo.userName}
            />
          </Form.Group>

          <Form.Group as={Col}>
            <Form.Label style={{ fontWeight: "bold" }}>Pokemon Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Pokemon"
              onChange={handlePokemon}
              value={userInfo.pokemonName}
            />
          </Form.Group>
        </Form.Row>

        <Button
          type="submit"
          style={{ width: "100%", marginTop: "40px" }}
          onClick={handleSubmit}
        >
          Add
        </Button>
      </Form>

      <h2
        style={{
          textAlign: "center",
          fontWeight: "bold",
          marginTop: "40px",
        }}
      >
        {validate ? "YOUR POKEMON DATA" : "CHOOSE YOUR POKEMON"}
      </h2>
      <Card style={{ marginTop: "20px" }}>
        <UserList user={userList} />
        <PrintData pokemon={pokemon} />
      </Card>
    </Card>
  );
};

export default DataForm;
