import React from 'react'
import {Card, Form} from 'react-bootstrap'
import UserList from './UserList';

const DataForm = () => {
    const [userName, setUserName] = useState();

    const handleUserName = (e) => {
        setUserName(e.target.value)
        console.log('User name is : ', e.target.value)
    }

    return (
        <Card style={{width: '25rem', padding: "20px"}}>

            <h1 style={{textAlign: 'center', fontWeight: "bold", marginBottom: "40px"}}>
                FORM
            </h1>
            <Form>
                <Form.Group controlId="formBasicPassword">
                    <Form.Label style={{fontWeight: 'bold'}}>User Name</Form.Label>
                    <Form.Control type="text" placeholder="Enter your name" onChange = {handleUserName} />

                    <UserList user={userName} />
                </Form.Group>
            </Form>
        </Card>
    )
}

export default DataForm
