import React, { useState, useEffect } from "react";
import { Card, Form, Button, Row, Col } from "react-bootstrap";
import Pokemon from "./Pokemon";
import UserList from "./UserList";

const DataForm = () => {
  const [userName, setUserName] = useState("");
  const [userList, setUserList] = useState([]);

  const handleUserName = (e) => {
    setUserName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (userName) {
      console.log(userName);
      let temp = userList;
      temp.push(userName);
      setUserList(temp);
      setUserName("");
    } else {
      console.log("Add some value");
    }
  };

  const unmount = () => {
    console.log("Component unmount called");
  };

  useEffect(() => {
    console.log("Data form useEffect");
    return unmount;
  }, []);

  return (
    <Card style={{ width: "50%", padding: "20px" }}>
      <h2
        style={{
          textAlign: "center",
          fontWeight: "bold",
          marginBottom: "40px",
        }}
      >
        FORM
      </h2>
      <Form>
        <Row>
          <Form.Group as={Col}>
            <Form.Label style={{ fontWeight: "bold" }}>User Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your name"
              onChange={handleUserName}
              value={userName}
            />

            <Form.Group as={Col}>
              <Form.Control
                type="text"
                placeholder="Enter your Favorite Pokemon"
                onChange={handleUserName}
                value={userName}
              />
            </Form.Group>

            <UserList user={userList} />
          </Form.Group>
        </Row>

        <Button type="submit" style={{ width: "100%" }} onClick={handleSubmit}>
          Add
        </Button>
      </Form>

      <h2
        style={{
          textAlign: "center",
          fontWeight: "bold",
          marginTop: "40px",
        }}
      >
        Favourite Pokemon
      </h2>
      <Card style={{ marginTop: "20px" }}>
        <Pokemon />
      </Card>
    </Card>
  );
};

export default DataForm;
